import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export const store = new Vuex.Store({
  state: {
    activeSongIndex: 0,
    songs: [{
      tittle: 'Nirvana',
      author: 'Nevermind',
      length: '3.15',
      cover: "https://1.allegroimg.com/s512/01cdf9/56dd602e43339ec08848ded18491"
    }, {
      tittle: "Everlong",
      author: 'Foo Fighters',
      length: '3.17',
      cover: "https://t2.genius.com/unsafe/300x300/https%3A%2F%2Fimages.genius.com%2F85e68eebd7bde3c972ed848aa74cb1e9.500x500x1.jpg"
    },
    {
      tittle: '99 problems ',
      author: 'Jay-z',
      length: '3.17',
      cover:"http://cdn.shopify.com/s/files/1/2036/5517/products/MUS000114546_600x.jpg?v=1528467286"
    },
    {
      tittle: 'Autobiografia',
      author: 'Perfect',
      length: '4.20',
      cover :"http://perfect.band/wp-content/uploads/bfi_thumb/Okladka_perfect-unu-1024x1024-neyennhsafodnyhwytkhyd10yqk4ulds11swvyh10w.jpg"
    },
    {
      tittle: 'Thriller',
      author: 'Michael Jackson',
      length: '3.15',
      cover:"https://images-na.ssl-images-amazon.com/images/I/51VRdpYNU9L.jpg"
    },
    {
      tittle: 'Hero of war',
      author: 'Rise against',
      length: '3.20'

    },
    {
      tittle: 'Witchcraft',
      author: 'Pendulum',
      length: '5.20',
      cover:"https://cps-static.rovicorp.com/3/JPG_500/MI0002/992/MI0002992994.jpg?partner=allrovi.com"
    }
    ],
    isPlayerActive: false
  },

  actions: {
    changePlayerState ({commit}) {
      commit('CHANGE_PLAYER_STATE')
    },
    selectActiveSongId ({commit}, id) {
      commit('SELECT_ACTIVE_SONG_ID', id)
    },
    changeSong ({commit}, d) {
      commit('CHANGE_SONG', d)
    }
  },
  mutations: {
    CHANGE_PLAYER_STATE (state) {
      state.isPlayerActive = !state.isPlayerActive
    },
    SELECT_ACTIVE_SONG_ID (state, id) {
      state.activeSongIndex = id
    },
    CHANGE_SONG (state, d) {
      state.activeSongIndex = state.activeSongIndex + d
      if (state.activeSongIndex >= state.songs.length) {
        state.activeSongIndex = 0
      } else if (state.activeSongIndex < 0) {
        state.activeSongIndex = state.songs.length - 1
      }
    }
  }

})
